(window.webpackJsonp=window.webpackJsonp||[]).push([[4],{184:function(n,t,e){n.exports=e.p+"assets/img/figure_2_1.0d642461.png"},188:function(n,t,e){"use strict";e.r(t);var a=e(0),r=Object(a.a)({},function(){var n=this,t=n.$createElement,a=n._self._c||t;return a("ContentSlotsDistributor",{attrs:{"slot-key":n.$parent.slotKey}},[a("h1",{attrs:{id:"гnава-2-древнее-искусство-интерактивной-художественной-nитературы"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#гnава-2-древнее-искусство-интерактивной-художественной-nитературы","aria-hidden":"true"}},[n._v("#")]),n._v(" Глава 2. Древнее искусство интерактивной художественной литературы")]),n._v(" "),a("p",[n._v("История визуальных новел начинается с середины 1970-х, а по другим данным с 1960-х годов. Сам термин «интерактивная\nхудожественная литература» означает программное обеспечение (то есть видеоигра), в которой взаимодействие с игроком\nосуществляется посредством текстовой информации. Эти игры оказали сильное влияние на становление нового жанра\nвизуальные новеллы. В свою очередь: визуальный роман (визуальная новелла) – это жанр компьютерных игр, подвид\nтекстового квеста, в котором зрителю демонстрируется история при помощи вывода на экран текста, статичных (либо\nанимированных) изображений, а также звукового или музыкального сопровождения.")]),n._v(" "),a("p",[n._v("В этой главе мы рассмотрим несколько интерактивных художественных произведений, ставшие основоположниками жанра\nвизуальных новел, и поговорим о программном обеспечении, которое использовалось для создания этих игр.")]),n._v(" "),a("h2",{attrs:{id:"прапрадед-визуаnьных-новеnn-eliza-1966"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#прапрадед-визуаnьных-новеnn-eliza-1966","aria-hidden":"true"}},[n._v("#")]),n._v(" Прапрадед визуальных новелл: ELIZA (1966)")]),n._v(" "),a("p",[n._v("Можно утверждать, что ELIZA сформировала основу интерфейса для всех будущих игр в жанре интерактивной литературы. Сама\nпрограмма представляла из себя виртуального психиатра с текстовым вводом данных. Она была создана в Массачусетском\nтехнологическом институте с 1964 по 1966 год Джозефом Вайценбаумом. Имея задатки искусственного интеллекта ELIZA\nпредоставляла примитивного, но иногда убедительного виртуального психотерапевта, который давал возможность людям\nвыговориться и поделиться своими проблемами.")]),n._v(" "),a("p",[n._v("ELIZA впервые появившись на огромном компьютере IBM 7094, а в последствии была перенесена на десятки других платформ.\nЗаявлялось, что она прошла теста Тьюринга (создан в 1950 году Аланом Тьюрингом), который проверяет, неотличимость\nвзаимодействие компьютера и его пользователя от взаимодействия человека с человеком.")]),n._v(" "),a("p",[n._v("В результате программа ELIZA заложила для будущих игр основные концепции взаимодействия игры с пользователем и\nосновные каноны оформления дизайна. Если сравнивать, то на 2019 год в основном игры имеют простой искусственный\nинтеллект, и не составляет конкуренцию пользователю игры. Возможно, если вы, будучи разработчиком углубитесь в изучении\nданной темы, то это может позволить сделать вашу игру уникальной и прорывной.")]),n._v(" "),a("h2",{attrs:{id:"дедушка-жанра-colossal-cave-adventure-1975"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#дедушка-жанра-colossal-cave-adventure-1975","aria-hidden":"true"}},[n._v("#")]),n._v(" Дедушка жанра: Colossal Cave Adventure (1975)")]),n._v(" "),a("p",[a("em",[n._v("Colossal Cave Adventure")]),n._v(" была первой работой в жанре интерактивной художественной литературы, созданная в 1975 году\nУиллом Кроутером, выпускником Массачусетского технологического института. Игра, которую часто называли "),a("em",[n._v("Adventure")]),n._v(",\nвыполнялась на гигантском компьютере PDP-10 (см. рисунок 2-1). Цель игры была проста: найти сокровища и выбраться из\nпещеры. В игре с текстовым интерфейсом насчитывалось 78 локаций.")]),n._v(" "),a("p",[a("img",{attrs:{src:e(184),alt:"Рисунок 2-1. Текстовое приветствие игры Adventure"}})]),n._v(" "),a("p",[a("strong",[n._v("Рисунок 2-1.")]),n._v(' Текстовое приветствие игры "Adventure"')]),n._v(" "),a("p",[n._v("В 1976 году выпускник Принстона Дон Вудс, после согласия Кроутера, доработал игру, добавив множество экзотических\nсуществ и предметов. Эти усовершенствования привели к ещё большей популяризации игры, вызвав лавину появления всё новых\nверсий игры, и перенос её на различные системы. Про неё писали в журналах, говоря, как о одной из лучших игр того\nвремени.")]),n._v(" "),a("h2",{attrs:{id:"первопроходцы"}},[a("a",{staticClass:"header-anchor",attrs:{href:"#первопроходцы","aria-hidden":"true"}},[n._v("#")]),n._v(" Первопроходцы")])])},[],!1,null,null,null);t.default=r.exports}}]);